var wrapper=document.querySelector(".wrapper"),radio_1=document.getElementById("radio-1"),radio_2=document.getElementById("radio-2"),radio_3=document.getElementById("radio-3");radio_1.addEventListener("change",function(){wrapper.classList.remove("background-slide-2");wrapper.classList.remove("background-slide-3");wrapper.classList.add("background-slide-1")});radio_2.addEventListener("change",function(){wrapper.classList.remove("background-slide-1");wrapper.classList.remove("background-slide-3");wrapper.classList.add("background-slide-2")});
radio_3.addEventListener("change",function(){wrapper.classList.remove("background-slide-1");wrapper.classList.remove("background-slide-2");wrapper.classList.add("background-slide-3")});
var feedback_open=document.querySelector(".button-feedback"),feedback=document.querySelector(".feedback-box"),feedback_overlay=document.querySelector(".feedback-overlay"),feedback_close=feedback.querySelector(".feedback-box-close"),feedback_form=feedback.querySelector("form"),feedback_name=feedback.querySelector("[name=name-feedback]"),feedback_email=feedback.querySelector("[name=email-feedback]"),feedback_commentary=feedback.querySelector("[name=commentary-feedback]");
feedback_open.addEventListener("click",function(a){a.preventDefault();feedback.classList.add("feedback-box-show");feedback_overlay.classList.add("feedback-overlay-show");feedback_name.focus()});feedback_close.addEventListener("click",function(a){a.preventDefault();feedback.classList.remove("feedback-box-show");feedback_overlay.classList.remove("feedback-overlay-show");feedback.classList.remove("feedback-box-error")});
feedback_overlay.addEventListener("click",function(){feedback.classList.remove("feedback-box-show");feedback_overlay.classList.remove("feedback-overlay-show");feedback.classList.remove("feedback-box-error")});feedback_form.addEventListener("submit",function(a){feedback_name.value&&feedback_email.value&&feedback_commentary.value||(a.preventDefault(),feedback.classList.remove("feedback-box-error"),feedback.offsetWidth=feedback.offsetWidth,feedback.classList.add("feedback-box-error"))});